#!/usr/bin/env python3

import socket
import sys
from typing import Dict, Optional, Tuple

class UDPKeyValueServer:
    def __init__(self, host: str = "0.0.0.0", port: int = 8080):
        self.host = host
        self.port = port
        self.data_store: Dict[str, str] = {}
        self.socket = None
        self.version = "Ken's Key-Value Store 1.0"

    def start(self):
        self.socket = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
        self.socket.bind((self.host, self.port))
        print(f"UDP Key-Value Server listening on {self.host}:{self.port}")

        try:
            while True:
                self._handle_request()
        except KeyboardInterrupt:
            print("\nShutting down server...")
        finally:
            if self.socket:
                self.socket.close()

    def _handle_request(self):
        try:
            data, client_address = self.socket.recvfrom(1000)
            message = data.decode('utf-8', errors='ignore')

            if len(data) >= 1000:
                return

            if '=' in message:
                self._handle_insert(message)
            else:
                self._handle_retrieve(message, client_address)

        except Exception as e:
            print(f"Error handling request: {e}")

    def _handle_insert(self, message: str):
        equals_index = message.find('=')
        if equals_index == -1:
            return

        key = message[:equals_index]
        value = message[equals_index + 1:]

        if key == "version":
            return

        self.data_store[key] = value

    def _handle_retrieve(self, key: str, client_address: Tuple[str, int]):
        if key == "version":
            value = self.version
        else:
            value = self.data_store.get(key, "")

        response = f"{key}={value}"
        response_bytes = response.encode('utf-8')

        if len(response_bytes) < 1000:
            try:
                self.socket.sendto(response_bytes, client_address)
            except Exception as e:
                print(f"Error sending response: {e}")

    def insert(self, key: str, value: str) -> bool:
        if key == "version":
            return False
        self.data_store[key] = value
        return True

    def retrieve(self, key: str) -> str:
        if key == "version":
            return self.version
        return self.data_store.get(key, "")

    def stop(self):
        if self.socket:
            self.socket.close()
            self.socket = None

def main():
    port = 8080
    if len(sys.argv) > 1:
        try:
            port = int(sys.argv[1])
        except ValueError:
            print("Invalid port number")
            sys.exit(1)

    server = UDPKeyValueServer(port=port)
    server.start()

if __name__ == "__main__":
    main()

