#!/usr/bin/env python3

import unittest
import socket
import threading
import time
from udp_server import UDPKeyValueServer

class TestUDPKeyValueServer(unittest.TestCase):
    def setUp(self):
        self.server = UDPKeyValueServer(host="127.0.0.1", port=0)
        self.server.socket = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
        self.server.socket.bind((self.server.host, 0))
        self.server_port = self.server.socket.getsockname()[1]

        self.server_thread = threading.Thread(target=self._run_server)
        self.server_thread.daemon = True
        self.server_thread.start()
        time.sleep(0.1)

        self.client_socket = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
        self.client_socket.settimeout(1.0)

    def tearDown(self):
        self.client_socket.close()
        self.server.stop()

    def _run_server(self):
        try:
            while self.server.socket:
                self.server._handle_request()
        except:
            pass

    def _send_and_receive(self, message: str) -> str:
        self.client_socket.sendto(message.encode('utf-8'), ('127.0.0.1', self.server_port))
        try:
            response, _ = self.client_socket.recvfrom(1000)
            return response.decode('utf-8')
        except socket.timeout:
            return None

    def _send_insert(self, message: str):
        self.client_socket.sendto(message.encode('utf-8'), ('127.0.0.1', self.server_port))
        time.sleep(0.1)

class TestInsertOperations(TestUDPKeyValueServer):
    def test_simple_insert(self):
        self._send_insert("foo=bar")
        self.assertEqual(self.server.retrieve("foo"), "bar")

    def test_insert_with_multiple_equals(self):
        self._send_insert("foo=bar=baz")
        self.assertEqual(self.server.retrieve("foo"), "bar=baz")

    def test_insert_empty_value(self):
        self._send_insert("foo=")
        self.assertEqual(self.server.retrieve("foo"), "")

    def test_insert_empty_key(self):
        self._send_insert("=foo")
        self.assertEqual(self.server.retrieve(""), "foo")

    def test_insert_equals_in_value(self):
        self._send_insert("foo===")
        self.assertEqual(self.server.retrieve("foo"), "==")

    def test_insert_update_existing_key(self):
        self._send_insert("key=value1")
        self._send_insert("key=value2")
        self.assertEqual(self.server.retrieve("key"), "value2")

    def test_insert_no_response(self):
        response = self._send_and_receive("test=value")
        self.assertIsNone(response)

class TestRetrieveOperations(TestUDPKeyValueServer):
    def test_retrieve_existing_key(self):
        self.server.insert("message", "Hello,world!")
        response = self._send_and_receive("message")
        self.assertEqual(response, "message=Hello,world!")

    def test_retrieve_nonexistent_key(self):
        response = self._send_and_receive("nonexistent")
        self.assertEqual(response, "nonexistent=")

    def test_retrieve_empty_key(self):
        self.server.insert("", "empty_key_value")
        response = self._send_and_receive("")
        self.assertEqual(response, "=empty_key_value")

    def test_retrieve_after_multiple_inserts(self):
        self._send_insert("key=value1")
        self._send_insert("key=value2")
        response = self._send_and_receive("key")
        self.assertEqual(response, "key=value2")

class TestVersionHandling(TestUDPKeyValueServer):
    def test_version_retrieve(self):
        response = self._send_and_receive("version")
        self.assertEqual(response, "version=Ken's Key-Value Store 1.0")

    def test_version_insert_ignored(self):
        self._send_insert("version=hacked")
        response = self._send_and_receive("version")
        self.assertEqual(response, "version=Ken's Key-Value Store 1.0")

    def test_version_not_empty(self):
        self.assertNotEqual(self.server.version, "")
        self.assertTrue(len(self.server.version) > 0)

class TestPacketSizeValidation(TestUDPKeyValueServer):
    def test_large_packet_ignored(self):
        large_message = "a" * 1000
        self.client_socket.sendto(large_message.encode('utf-8'), ('127.0.0.1', self.server_port))
        time.sleep(0.1)

        response = self._send_and_receive("test")
        self.assertEqual(response, "test=")

    def test_response_size_limit(self):
        long_value = "x" * 990
        self.server.insert("test", long_value)
        response = self._send_and_receive("test")
        self.assertIsNotNone(response)

class TestEdgeCases(TestUDPKeyValueServer):
    def test_special_characters_in_key(self):
        self._send_insert("key with spaces=value")
        response = self._send_and_receive("key with spaces")
        self.assertEqual(response, "key with spaces=value")

    def test_unicode_characters(self):
        self._send_insert("café=☕")
        response = self._send_and_receive("café")
        self.assertEqual(response, "café=☕")

    def test_key_with_newlines(self):
        key_with_newline = "key\nwith\nnewline"
        value = "test_value"
        self._send_insert(f"{key_with_newline}={value}")
        response = self._send_and_receive(key_with_newline)
        self.assertEqual(response, f"{key_with_newline}={value}")

class TestDirectServerMethods(unittest.TestCase):
    def setUp(self):
        self.server = UDPKeyValueServer()

    def test_insert_method(self):
        result = self.server.insert("test", "value")
        self.assertTrue(result)
        self.assertEqual(self.server.retrieve("test"), "value")

    def test_insert_version_blocked(self):
        result = self.server.insert("version", "hacked")
        self.assertFalse(result)
        self.assertEqual(self.server.retrieve("version"), "Ken's Key-Value Store 1.0")

    def test_retrieve_method(self):
        self.server.insert("key", "value")
        result = self.server.retrieve("key")
        self.assertEqual(result, "value")

    def test_retrieve_nonexistent(self):
        result = self.server.retrieve("nonexistent")
        self.assertEqual(result, "")

if __name__ == "__main__":
    unittest.main()

